INSERT INTO `wp_v6yg8f8frk_posts` VALUES ('18','0','2020-10-24 17:03:12','2020-10-24 17:03:12','<!-- wp:coblocks/hero {\"marginBottom\":0,\"marginBottomTablet\":0,\"marginBottomMobile\":0,\"backgroundColor\":\"tertiary\",\"heightMobile\":400,\"maxWidth\":805,\"coblocks\":{\"id\":\"71414479560\"}} -->\n<div class=\"wp-block-coblocks-hero alignfull coblocks-hero-71414479560\"><div class=\"wp-block-coblocks-hero__inner has-tertiary-background-color has-background hero-center-left-align has-padding has-huge-padding has-tertiary-background-color\" style=\"min-height:500px\"><div class=\"wp-block-coblocks-hero__content-wrapper\"><div class=\"wp-block-coblocks-hero__content\" style=\"max-width:805px\"><!-- wp:heading {\"level\":1} -->\n<h1>Thomas Vazhakunnathu</h1>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Spiritual scientist, teacher and author</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:buttons {\"align\":\"left\"} -->\n<div class=\"wp-block-buttons alignleft\"><!-- wp:button -->\n<div class=\"wp-block-button\"><a class=\"wp-block-button__link\" href=\"https://thomasvazhakunnathu.com/about/\">About Me</a></div>\n<!-- /wp:button -->\n\n<!-- wp:button {\"className\":\"is-style-outline\"} -->\n<div class=\"wp-block-button is-style-outline\"><a class=\"wp-block-button__link\" href=\"https://thomasvazhakunnathu.com/contact/\">Contact Me</a></div>\n<!-- /wp:button --></div>\n<!-- /wp:buttons --></div></div></div></div>\n<!-- /wp:coblocks/hero -->\n\n<!-- wp:columns {\"align\":\"wide\"} -->\n<div class=\"wp-block-columns alignwide\"><!-- wp:column {\"width\":66.66} -->\n<div class=\"wp-block-column\" style=\"flex-basis:66.66%\"><!-- wp:heading -->\n<h2>My Message</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>With a school-age daughter of my own, it is of the utmost importance that we keep pushing for changes in our schools, better study spaces for our kids, affordable after-school programs, and healthy lunches. Our children are the next generation, let\'s make sure they\'re prepared, together.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>I have been actively serving our community since before my children began school. As a leader of the PTA, youth sports, and fundraising committees, I am passionate about making our town a better place for our children - and for the future.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column -->\n\n<!-- wp:column {\"width\":33.33} -->\n<div class=\"wp-block-column\" style=\"flex-basis:33.33%\"></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:image {\"align\":\"wide\",\"id\":4,\"sizeSlug\":\"large\"} -->\n<figure class=\"wp-block-image alignwide size-large\"><img src=\"https://thomasvazhakunnathu.com/wp-content/uploads/2020/10/home-image-1.jpg\" alt=\"Image description\" class=\"wp-image-4\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:columns {\"align\":\"wide\"} -->\n<div class=\"wp-block-columns alignwide\"><!-- wp:column {\"width\":20} -->\n<div class=\"wp-block-column\" style=\"flex-basis:20%\"></div>\n<!-- /wp:column -->\n\n<!-- wp:column {\"width\":80} -->\n<div class=\"wp-block-column\" style=\"flex-basis:80%\"><!-- wp:quote {\"className\":\"is-style-large\"} -->\n<blockquote class=\"wp-block-quote is-style-large\"><p>Successful people do what unsuccessful people are simply unwilling to do.</p><cite>- Jesse Doe</cite></blockquote>\n<!-- /wp:quote --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:coblocks/features {\"contentAlign\":\"left\",\"className\":\"alignwide\",\"coblocks\":{\"id\":\"7142041440\"},\"gutter\":\"large\"} -->\n<div class=\"wp-block-coblocks-features coblocks-features-7142041440 alignwide\"><div class=\"alignwide wp-block-coblocks-features__inner has-columns has-2-columns has-responsive-columns has-left-content has-large-gutter\"><!-- wp:coblocks/feature {\"coblocks\":{\"id\":\"71420414465\"}} -->\n<div class=\"wp-block-coblocks-feature coblocks-feature-71420414465\"><div class=\"wp-block-coblocks-feature__inner has-no-pa